import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import {
  Container,
  Grid,
  Card,
  CardContent,
  Typography,
  Button,
  IconButton,
  Modal,
  Box,
  TextField,
  Checkbox,
  AppBar,
  Toolbar,
} from '@mui/material';
import {
  Add as AddIcon,
  Close as CloseIcon,
  Check as CheckIcon,
  Logout as LogoutIcon,
} from '@mui/icons-material';
import { useAuth } from '../context/AuthContext';
import axios from 'axios';

const modalStyle = {
  position: 'absolute',
  top: '50%',
  left: '50%',
  transform: 'translate(-50%, -50%)',
  width: 400,
  bgcolor: 'background.paper',
  boxShadow: 24,
  p: 4,
  borderRadius: 2,
};

const Dashboard = () => {
  const [todos, setTodos] = useState([]);
  const [selectedTodo, setSelectedTodo] = useState(null);
  const [openModal, setOpenModal] = useState(false);
  const [newTodo, setNewTodo] = useState({ title: '', subtitle: '' });
  const { user, logout } = useAuth();
  const navigate = useNavigate();

  useEffect(() => {
    fetchTodos();
  }, []);

  const fetchTodos = async () => {
    try {
      const response = await axios.get('http://localhost:5000/api/todos');
      setTodos(response.data);
    } catch (error) {
      console.error('Error fetching todos:', error);
    }
  };

  const handleLogout = () => {
    logout();
    navigate('/login');
  };

  const handleOpenModal = (todo = null) => {
    setSelectedTodo(todo);
    if (todo) {
      setNewTodo({ title: todo.title, subtitle: todo.subtitle });
    } else {
      setNewTodo({ title: '', subtitle: '' });
    }
    setOpenModal(true);
  };

  const handleCloseModal = () => {
    setOpenModal(false);
    setSelectedTodo(null);
    setNewTodo({ title: '', subtitle: '' });
  };

  const handleSaveTodo = async () => {
    try {
      if (selectedTodo) {
        await axios.put(`http://localhost:5000/api/todos/${selectedTodo.id}`, {
          ...newTodo,
          isCompleted: selectedTodo.is_completed,
        });
      } else {
        await axios.post('http://localhost:5000/api/todos', newTodo);
      }
      fetchTodos();
      handleCloseModal();
    } catch (error) {
      console.error('Error saving todo:', error);
    }
  };

  const handleDeleteTodo = async (id) => {
    try {
      await axios.delete(`http://localhost:5000/api/todos/${id}`);
      fetchTodos();
    } catch (error) {
      console.error('Error deleting todo:', error);
    }
  };

  const handleToggleComplete = async (todo) => {
    try {
      await axios.put(`http://localhost:5000/api/todos/${todo.id}`, {
        ...todo,
        isCompleted: !todo.is_completed,
      });
      fetchTodos();
    } catch (error) {
      console.error('Error updating todo:', error);
    }
  };

  return (
    <>
      <AppBar position="static">
        <Toolbar>
          <Typography variant="h6" component="div" sx={{ flexGrow: 1 }}>
            Todo List
          </Typography>
          <Typography variant="subtitle1" sx={{ mr: 2 }}>
            Welcome, {user?.username}
          </Typography>
          <IconButton color="inherit" onClick={handleLogout}>
            <LogoutIcon />
          </IconButton>
        </Toolbar>
      </AppBar>

      <Container sx={{ mt: 4 }}>
        <Grid container spacing={3}>
          <Grid item xs={12}>
            <Button
              variant="contained"
              startIcon={<AddIcon />}
              onClick={() => handleOpenModal()}
            >
              Add New Todo
            </Button>
          </Grid>

          {todos.map((todo) => (
            <Grid item xs={12} sm={6} md={4} key={todo.id}>
              <Card
                sx={{
                  height: '100%',
                  display: 'flex',
                  flexDirection: 'column',
                  cursor: 'pointer',
                  '&:hover': {
                    boxShadow: 6,
                  },
                }}
                onClick={() => handleOpenModal(todo)}
              >
                <CardContent>
                  <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
                    <Typography variant="h6" component="div">
                      {todo.title}
                    </Typography>
                    <Checkbox
                      checked={todo.is_completed}
                      onChange={(e) => {
                        e.stopPropagation();
                        handleToggleComplete(todo);
                      }}
                      icon={<CheckIcon />}
                      checkedIcon={<CheckIcon />}
                    />
                  </Box>
                  {todo.subtitle && (
                    <Typography variant="body2" color="text.secondary" sx={{ mt: 1 }}>
                      {todo.subtitle}
                    </Typography>
                  )}
                </CardContent>
              </Card>
            </Grid>
          ))}
        </Grid>
      </Container>

      <Modal
        open={openModal}
        onClose={handleCloseModal}
        aria-labelledby="todo-modal-title"
      >
        <Box sx={modalStyle}>
          <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', mb: 2 }}>
            <Typography id="todo-modal-title" variant="h6" component="h2">
              {selectedTodo ? 'Edit Todo' : 'New Todo'}
            </Typography>
            <IconButton onClick={handleCloseModal}>
              <CloseIcon />
            </IconButton>
          </Box>
          <TextField
            fullWidth
            label="Title"
            value={newTodo.title}
            onChange={(e) => setNewTodo({ ...newTodo, title: e.target.value })}
            margin="normal"
          />
          <TextField
            fullWidth
            label="Subtitle"
            value={newTodo.subtitle}
            onChange={(e) => setNewTodo({ ...newTodo, subtitle: e.target.value })}
            margin="normal"
          />
          <Box sx={{ mt: 2, display: 'flex', justifyContent: 'space-between' }}>
            <Button
              variant="contained"
              color="primary"
              onClick={handleSaveTodo}
            >
              Save
            </Button>
            {selectedTodo && (
              <Button
                variant="contained"
                color="error"
                onClick={() => {
                  handleDeleteTodo(selectedTodo.id);
                  handleCloseModal();
                }}
              >
                Delete
              </Button>
            )}
          </Box>
        </Box>
      </Modal>
    </>
  );
};

export default Dashboard;
